    return callApi('PATCH', `/apps/${APP_IDS.TEILNEHMERANMELDUNG}/records/${id}`, { fields });
  }
  static async deleteTeilnehmeranmeldungEntry(id: string) {
    return callApi('DELETE', `/apps/${APP_IDS.TEILNEHMERANMELDUNG}/records/${id}`);
  }

